#include "get_next_line.h"


int	read_to_vault(int fd, t_list **vault)
{
	char	*buffer;
	ssize_t	bytes_read;
	char	*buffer_copy;
	t_list	*current;

	buffer = (char *)malloc(sizeof(char) * (BUFFER_SIZE + 1));
	if (!buffer)
		return (0);
	bytes_read = 1;
	current = NULL;
	while (!found_newline(current) && bytes_read > 0) //can use ftstrchr later: ft_strchr(current->content, '\n')
	{
		bytes_read = read(fd, buffer, BUFFER_SIZE);
		if (bytes_read <= 0)
			break;
		buffer[bytes_read] = '\0';
		buffer_copy = ft_strdup(buffer);
		if (!buffer_copy)
			return (free(buffer), -1);
		lst_add_back(&current, lst_new(buffer_copy));
	}
	*vault = current;
	return (free(buffer),0);
}

size_t	line_len(t_list *vault)
{
	size_t	len;
	size_t	i;

	len = 0;
	while (vault)
	{
		i = 0;
		while (vault->content[i])
		{
			if (vault->content[i] == '\n')
				return (len + 1);
			len++;
			i++;
		}
		vault = vault->next;
	}
	return (len);
}

char	*assemble_line(t_list *vault)
{
	char	*line;
	size_t	i;
	size_t	j;
	size_t	len;

	if (!vault)
		return (NULL);
	len = line_len(vault);
	line = (char *)malloc(sizeof(char) * (len + 1));
	if (!line)
		return (NULL);
	i = 0;
	while (vault)
	{
		j = 0;
		while (vault->content[j] && i < len)
		{
			line[i] = vault->content[j];
			i++;
			j++;
		}
		vault = vault->next;
	}
	line[i] = '\0';
	return (line);
}

void	ft_lstclear(t_list **lst, void (*del)(void *))
{
	t_list	*ptr;
	t_list	*temp;

	if (!lst || !del)
		return ;
	ptr = *lst;
	while (ptr != NULL)
	{
		temp = ptr;
		if (del)
			del(ptr->content);
		ptr = ptr->next;
		free(temp);
	}
	*lst = NULL;
}

void	update(t_list **vault)
{
	t_list	*last;
	t_list	*new_vault_head;
	// char	*leftover_chars;
	int		i;

	if (!vault || !*vault)
		return ;

	//find the last
	new_vault_head = NULL;
	last = *vault;
	while (last && !ft_strchr(last->content, '\n'))
		last = last->next;
	if (!last)
		return (ft_lstclear(vault, free));

	//find the length of the last line
	i = 0;
	while (last->content[i] && last->content[i] != '\n')
		i++;
	if (last->content[i] == '\n')
		i++;

	if (last->content[i])
		new_vault_head = lst_new(ft_substr(last->content, i, ft_strlen(last->content) - i));

	// Case 3: There are more nodes after the 'last_node'.
	if (last->next)
	{
		if (new_vault_head == NULL) // If no remainder, the next node is the new head.
			new_vault_head = last->next;
		else // If there was a remainder, append the rest of the list to it.
			lst_add_back(&new_vault_head, last->next);
		last->next = NULL; // Detach the rest of the list.
	}

	//create a new vault with the leftover characters
	ft_lstclear(vault, free);
	*vault = new_vault_head;
}

char	*get_next_line(int fd)
{
	static t_list	*vault;
	char			*the_line;

	if (fd < 0 || BUFFER_SIZE <= 0 || read(fd, 0, 0) < 0)
		return (NULL);

	if (read_to_vault(fd, &vault) == -1)
		return (ft_lstclear(&vault, free), NULL);
	if (!vault)
		return (NULL);

	the_line = assemble_line(vault);
	if (!the_line)
		return (ft_lstclear(&vault, free), NULL);
	if (the_line && *the_line == '\0' && !found_newline(vault))
	{
		free(the_line);
		ft_lstclear(&vault, free);
		return (NULL);
	}
	update(&vault);

	return (the_line);
}

